   const elementContainer = document.getElementById('scroll-div');
   const scrollingElements = document.querySelectorAll('.intro');
    let currentIndex = 0;

    document.querySelector(".arrow-bar").addEventListener("click", e =>{
        if(e.target.matches("#next")){
            scrollheft()
        }
        if(e.target.matches("#prev")){
            scrollRight()
        }
    })
  

      function scrollheft() {
        if (currentIndex < scrollingElements.length - 1) {
          currentIndex++;
          updateTransform();
        }
      }
  
      function scrollRight() {
        if (currentIndex > 0) {
          currentIndex--;
          updateTransform();
        }
      }
  
      function updateTransform() {
        const translation = -currentIndex * 350; // Adjust the scroll amount (width + margin) as needed
        elementContainer.style.transform = `translateX(${translation}px)`;
      }


      